query:
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX cdm: <http://publications.europa.eu/ontology/cdm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT DISTINCT ("Publication Themes Classification" AS ?publicationsClassification) ?publicationsClassification_uri ?opTheme ?opTheme_uri ?opTheme_dateCreated
where
    {
{?opTheme_uri rdf:type ?publicationsClassification_uri;
skos:prefLabel ?opTheme;
dcterms:created ?opTheme_dateCreated.
#?publicationsClassification_uri rdfs:label ?publicationsClassification                    
}
filter(?publicationsClassification_uri=<http://publications.europa.eu/ontology/cdm#concept_op_theme>)
FILTER(langMatches(lang(?opTheme), "en")).
filter not exists {
?opTheme_uri skos:broader ?broader
}
}ORDER BY ?publicationsClassification ?opTheme

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?eurovocConcept ?eurovocConcept_uri ?eurovocConcept_definition ?aligment (?aligment AS ?aligment_uri) (?aligment AS ?aligment_link) WHERE
{
?eurovocConcept_uri skos:inScheme <http://eurovoc.europa.eu/100141> ;
skos:prefLabel ?eurovocConcept.
OPTIONAL{
    ?eurovocConcept_uri skos:definition ?eurovocConcept_definition.
}
{?eurovocConcept_uri skos:exactMatch ?aligment}
UNION
{?eurovocConcept_uri skos:closeMatch ?aligment}
FILTER(langMatches(lang(?eurovocConcept), "en")).
FILTER(langMatches(lang(?eurovocConcept_definition), "en")).
filter(?eurovocConcept_uri=<PARAMETER>).
}ORDER BY ?eurovocConcept ?aligment

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 
    PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
            SELECT DISTINCT ?taxonomyLevel1 ?taxonomyLevel1_uri ?taxonomyLevel2 ?taxonomyLevel2_uri
                    WHERE{{
                    ?taxonomyLevel1_uri skos:prefLabel ?taxonomyLevel1;
                                         skos:narrower ?taxonomyLevel2_uri.
                    ?taxonomyLevel2_uri skos:prefLabel ?taxonomyLevel2
                    } 
                   filter(?taxonomyLevel1_uri=<PARAMETER>).
                   FILTER(langMatches(lang(?taxonomyLevel2), "en"))
                   FILTER(langMatches(lang(?taxonomyLevel1), "en"))
                   }ORDER BY ?taxonomyLevel1 ?taxonomyLevel2

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 
    PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
            SELECT DISTINCT ?detLevel2 ?detLevel2_uri ?detLevel3 ?detLevel3_uri
                    WHERE{{
                    ?detLevel2_uri skos:prefLabel ?detLevel2;
                                         skos:narrower ?detLevel3_uri.
                    ?detLevel3_uri skos:prefLabel ?detLevel3
                    } 
                   filter(?detLevel2_uri=<PARAMETER>).
                   FILTER(langMatches(lang(?detLevel2), "en"))
                   FILTER(langMatches(lang(?detLevel3), "en"))
                   }ORDER BY ?detLevel2 ?detLevel3

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT DISTINCT ?detLevel1 ?detLevel1_uri ?eurovocConcept ?eurovocConcept_uri ?eurovocConcept_definition
where {{?detLevel1_uri skos:prefLabel ?detLevel1;
skos:exactMatch ?eurovocConcept_uri.
?eurovocConcept_uri skos:prefLabel ?eurovocConcept.
OPTIONAL{
?eurovocConcept_uri skos:definition ?eurovocConcept_definition.
FILTER(langMatches(lang(?eurovocConcept_definition), "en")).
}
} 
filter(?detLevel1_uri=<PARAMETER>).
FILTER(langMatches(lang(?detLevel1), "en")).
FILTER(langMatches(lang(?eurovocConcept), "en")).
}

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX cdm: <http://publications.europa.eu/ontology/cdm#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX geo: <http://www.opengis.net/ont/geosparql#>
PREFIX authority: <http://publications.europa.eu/ontology/authority/>
PREFIX org: <http://www.w3.org/ns/org#>
SELECT DISTINCT ?country_uri ?country ?corporateBody ?corporateBody_uri ?corporateBody_desc
where {{
?country_uri skos:prefLabel ?country;
authority:deprecated ?deprecated.
?country_uri org:hasMembership ?o.
OPTIONAL {
?o org:organization ?corporateBody_uri.
?corporateBody_uri authority:deprecated ?deprecated2;
authority:authority-code ?corporateBody;
skos:prefLabel ?corporateBody_desc.
filter(?deprecated2="false").
FILTER(langMatches(lang(?corporateBody_desc), "en")).
}
}
FILTER(?country_uri=<PARAMETER>)
FILTER(langMatches(lang(?country), "en")).
filter(?deprecated="false").
}

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
            PREFIX dcterms: <http://purl.org/dc/terms/>
            PREFIX euvoc: <http://publications.europa.eu/ontology/euvoc#>
            PREFIX authority: <http://publications.europa.eu/ontology/authority/>
                    SELECT DISTINCT ?org ?org_uri ?org_desc ?treaty ?startDate ?endDate ?replaces
                    where {{?org_uri skos:prefLabel ?org_desc;
                                      authority:authority-code ?org;
                                      euvoc:startDate ?startDate.
                            OPTIONAL {
                            ?org_uri euvoc:endDate ?endDate.
                            }
                            OPTIONAL{
                            ?org_uri euvoc:legallyBasedOnTreaty ?treaty_code.
                            ?treaty_code skos:prefLabel ?treaty.
                            FILTER(langMatches(lang(?treaty), "en")).
                            }
                            OPTIONAL{
                            ?org_uri dcterms:replaces ?replaces.
                            }
                            } 
                           filter(?org_uri=<PARAMETER>).
                           FILTER(langMatches(lang(?org_desc), "en")).
                           }

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
 PREFIX org: <http://www.w3.org/ns/org#>
 PREFIX foaf: <http://xmlns.com/foaf/0.1/>
    PREFIX euvoc: <http://publications.europa.eu/ontology/euvoc#>
 SELECT DISTINCT ?corporateBody ?directoryOrg ?directoryOrg_uri ?directoryOrg_image ?directoryOrg_order ?directoryOrg_protocolLevel ?directoryOrg_email ?directoryOrg_telephone ?directoryOrg_locality ?directoryOrg_postalCode ?directoryOrg_streetAddress ?directoryOrg_homepage
            where {{?corporateBody_uri skos:prefLabel ?corporateBody.
                    ?directoryOrg_uri org:subOrganizationOf ?corporateBody_uri. 
 OPTIONAL {?directoryOrg_uri foaf:depiction ?directoryOrg_image.}
                   ?directoryOrg_uri skos:prefLabel ?directoryOrg.
                    OPTIONAL {
                    ?directoryOrg_uri euvoc:order ?directoryOrg_order.
                    }
                    OPTIONAL {
                    ?directoryOrg_uri euvoc:protocolLevel ?directoryOrg_protocolLevel.
                    }
                      #OPTIONAL {
                    #?directoryOrg_contactPoint foaf:homepage ?directoryOrg_homepage.
                    #}
                      } 
                   filter(?corporateBody_uri=<PARAMETER>).
                   FILTER(langMatches(lang(?directoryOrg), "en")).
                   FILTER(langMatches(lang(?corporateBody), "en")).
                   }
                   #ORDER BY ?whoiswhoOrg ?country ?location

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX org: <http://www.w3.org/ns/org#>
PREFIX euvoc: <http://publications.europa.eu/ontology/euvoc#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX resource: <http://publications.europa.eu/resource/>
PREFIX vcard:<http://www.w3.org/2006/vcard/ns#>
SELECT DISTINCT ?membership ?membership_uri ?person ?person_uri (?person AS ?person_name) ?person_givenName ?person_familyName ?person_gender ?person_genderUri ?person_citiz ?person_image ?person_id
#select distinct ?p ?o
where {{?membership_uri org:organization ?directoryOrg_uri;
euvoc:positionComplement ?membership.
?directoryOrg_uri skos:prefLabel ?directoryOrg.
?person_uri org:hasMembership ?membership_uri;
foaf:givenName ?person_givenName;
foaf:familyName ?person_familyName;
foaf:name ?person.
#OPTIONAL{
#?person_uri vcard:hasHonorificPrefix ?person_honPrefix
#}
OPTIONAL{
?person_uri vcard:hasGender ?person_genderURI.
?person_genderURI skos:prefLabel ?person_gender.
FILTER(langMatches(lang(?person_gender), "en")).
}
OPTIONAL{
?person_uri resource:personcitizenship ?person_citizUri.
?person_citizUri skos:prefLabel ?person_citiz.
FILTER(langMatches(lang(?person_citiz), "en")).
}
OPTIONAL{
?person_uri foaf:img ?person_image.
BIND (STRBEFORE(STRAFTER( STR(?person_image), "https://www.europarl.europa.eu/mepphoto/" ), ".jpg" ) AS ?person_id)
}
}
FILTER(STR(?membership)='PARAMETER').
filter(?directoryOrg_uri=<PARAMETER2>).
FILTER(langMatches(lang(?membership), "en")).
FILTER(langMatches(lang(?directoryOrg), "en")).
                   }

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX cdm: <http://publications.europa.eu/ontology/cdm#>
PREFIX cmr: <http://publications.europa.eu/ontology/cdm/cmr#>
SELECT DISTINCT ?expression ?expression_uri ?manifestation ?manifestation_uri ?manifestation_image ?manifestation_number (CONCAT(STR(DAY(?date)),"/", STR(MONTH(?date)), "/", STR(YEAR(?date))) as ?manifestation_creationDate) (CONCAT(STR(DAY(?date2)),"/", STR(MONTH(?date2)), "/", STR(YEAR(?date2))) as ?manifestation_lastModificationDate) ?manifestation_link
where {
{
?manifestation_uri cdm:manifestation_manifests_expression ?expression_uri.
?manifestation_uri cdm:manifestation_has_thumbnail ?manifestation_image;
cdm:manifestation_number_catalogue ?manifestation_number;
cmr:creationDate ?date;
cmr:lastModificationDate ?date2;
cdm:manifestation_type ?manifestation.
?expression_uri cdm:expression_title ?expression.
OPTIONAL{
     ?manifestation_link cdm:item_belongs_to_manifestation ?manifestation_uri;
     cmr:manifestationMimeType ?mimeType.
     filter((?mimeType='application/pdf;type=pdfx')||(?mimeType='application/pdf')).
}
}
filter(?expression_uri=<PARAMETER>)
}ORDER BY ?expression DESC(?date)

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX cdm: <http://publications.europa.eu/ontology/cdm#>
SELECT DISTINCT ?corporateBody ?corporateBody_uri ?publicationDocument ?publicationDocument_uri ?publicationDocument_abstract ?publicationDocument_language ?publicationDocument_date ?publicationDocument_id
where {{?publicationDocument_documentUri cdm:publication_general_is_about_concept_op_theme ?opTheme_uri;
cdm:work_authored_by_agent ?corporateBody_uri.
?opTheme_uri skos:prefLabel ?opTheme.
?corporateBody_uri skos:prefLabel ?corporateBody.
?publicationDocument_documentUri cdm:work_date_document ?publicationDocument_date;
cdm:work_id_document ?publicationDocument_id.
?publicationDocument_uri cdm:expression_belongs_to_work ?publicationDocument_documentUri;
cdm:expression_uses_language ?expression_languageUri;
cdm:expression_title ?publicationDocument.
?expression_languageUri skos:prefLabel ?publicationDocument_language.
OPTIONAL{
?publicationDocument_uri cdm:expression_abstract ?publicationDocument_abstract.
}
} 
filter(?opTheme_uri=<PARAMETER2>)
filter(?corporateBody_uri=<PARAMETER>)
FILTER(langMatches(lang(?corporateBody), "en")).
FILTER(langMatches(lang(?opTheme), "en")).
FILTER(?expression_languageUri=<http://publications.europa.eu/resource/authority/language/ENG>).
FILTER(langMatches(lang(?publicationDocument_language), "en")).
}ORDER BY ?corporateBody DESC(?publicationDocument_date)

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX org: <http://www.w3.org/ns/org#>
PREFIX euvoc: <http://publications.europa.eu/ontology/euvoc#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX resource: <http://publications.europa.eu/resource/>
SELECT DISTINCT ?membership ?membership_order ?membership_positionStatus ?person ?person_image 

where {{?membership_uri org:organization ?directoryOrg_uri;
                        euvoc:order ?membership_order;
                        euvoc:positionComplement ?membership;
                        euvoc:positionStatus ?membership_positionStatusUri.
    ?directoryOrg_uri skos:prefLabel ?directoryOrg.
    ?membership_positionStatusUri skos:prefLabel ?membership_positionStatus.
    ?person_uri org:hasMembership ?membership_uri;
                foaf:name ?person.
OPTIONAL{
    ?person_uri foaf:img ?person_image.
    BIND (STRBEFORE(STRAFTER( STR(?person_image), "https://www.europarl.europa.eu/mepphoto/" ), ".jpg" ) AS ?person_id)
    }
    } 
filter(?directoryOrg_uri=<PARAMETER>).
FILTER(langMatches(lang(?membership), "en")).
FILTER(langMatches(lang(?membership_positionStatus), "en")).
FILTER(langMatches(lang(?directoryOrg), "en")).
}ORDER BY ?membership_order ?person

PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX org: <http://www.w3.org/ns/org#>
PREFIX euvoc: <http://publications.europa.eu/ontology/euvoc#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX resource: <http://publications.europa.eu/resource/>
SELECT DISTINCT ?membership ?membership_order ?membership_positionStatus ?person ?person_citiz ?person_image
where {{?membership_uri org:organization ?directoryOrg_uri;
euvoc:order ?membership_order;
euvoc:positionComplement ?membership;
euvoc:positionStatus ?membership_positionStatusUri.
?directoryOrg_uri skos:prefLabel ?directoryOrg.
?membership_positionStatusUri skos:prefLabel ?membership_positionStatus.
?person_uri org:hasMembership ?membership_uri;
foaf:name ?person.
OPTIONAL{
?person_uri resource:personcitizenship ?person_citizUri.
?person_citizUri skos:prefLabel ?person_citiz.
FILTER(langMatches(lang(?person_citiz), "en")).
}
OPTIONAL{
?person_uri foaf:img ?person_image.
BIND (STRBEFORE(STRAFTER( STR(?person_image), "https://www.europarl.europa.eu/mepphoto/" ), ".jpg" ) AS ?person_id)
}
}
filter(?directoryOrg_uri=<PARAMETER>).
FILTER(langMatches(lang(?membership), "en")).
FILTER(langMatches(lang(?membership_positionStatus), "en")).
FILTER(langMatches(lang(?directoryOrg), "en")).
}ORDER BY ?membership_order ?person

PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT DISTINCT ('data.europa.eu' as ?dataeuropa) ('http://data.europa.eu' as ?dataeuropa_uri) concat(?title, ' - ', ?description) as ?DEUCatalog ?DEUCatalog_uri ?DEUCatalog_Modified ?DEUCatalog_Language  
  WHERE{ ?DEUCatalog_uri <http://purl.org/dc/terms/title>  ?title
  OPTIONAL
  { ?DEUCatalog_uri <http://purl.org/dc/terms/description>  ?description
  } ?DEUCatalog_uri <http://purl.org/dc/terms/modified> ?DEUCatalog_Modified. ?DEUCatalog_uri <http://purl.org/dc/terms/language> ?DEUCatalog_Language   { SELECT  ?DEUCatalog_uri 
        WHERE
          { ?DEUCatalog_uri a <http://www.w3.org/ns/dcat#Catalog>}
        }
        } ORDER BY ?dataeuropa ?DEUCatalog

PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT DISTINCT "Data catalog" as ?DEUCatalogRoot ?DEUCatalog_Modified ?DEUCatalog_Language ?DEUCatalog_uri concat(?title, ' - ', ?description) as ?DEUCatalog  ?viz_theme ?viz_distribution ?viz_dataset ?viz ?viz_accessURL
  WHERE{{ ?DEUCatalog_uri <http://purl.org/dc/terms/title>  ?title;
                         dcat:dataset ?viz_dataset.
        ?viz_dataset dcat:distribution ?viz_distribution;
           dcat:theme ?viz_theme;
           dcterms:publisher ?viz_publisher.
        ?viz_distribution dcterms:title ?viz;
              dcterms:type <http://publications.europa.eu/resource/authority/distribution-type/VISUALIZATION>;
              dcat:accessURL ?viz_accessURL
  OPTIONAL
  { ?DEUCatalog_uri <http://purl.org/dc/terms/description>  ?description
  } ?DEUCatalog_uri <http://purl.org/dc/terms/modified> ?DEUCatalog_Modified. ?DEUCatalog_uri <http://purl.org/dc/terms/language> ?DEUCatalog_Language   { SELECT  ?DEUCatalog_uri 
        WHERE
          { ?DEUCatalog_uri a <http://www.w3.org/ns/dcat#Catalog>}
        }}
        filter(?DEUCatalog_uri=<PARAMETER>).
        }

PREFIX skos:<http://www.w3.org/2004/02/skos/core#>
SELECT distinct ?treatyClassification_uri "TREATY CLASSIFICATION" as ?treatyClassification ?treatyType ?treatyType_uri ?treatyType_definition WHERE
{{ 
?treatyType_uri skos:topConceptOf ?treatyClassification_uri;
skos:prefLabel ?treatyType;
skos:definition ?treatyType_definition.
} 
FILTER (?treatyClassification_uri=<http://publications.europa.eu/resource/authority/treaty-classification>).
FILTER(langMatches(lang(?treatyType), "en")).
FILTER(langMatches(lang(?treatyType_definition), "en")).}

PREFIX skos:<http://www.w3.org/2004/02/skos/core#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX euvoc: <http://publications.europa.eu/ontology/euvoc#>
PREFIX ontology:<http://data.europa.eu/eli/ontology#>
PREFIX geosparql:<http://www.opengis.net/ont/geosparql#>
PREFIX authority:<http://publications.europa.eu/ontology/authority/>

SELECT distinct ?treatyType ?treatyType_uri ?treatyType_definition ?treaty ?treaty_uri ?treaty_startDate ?treaty_dateEIF ?treaty_OJNumber ?treaty_OJSeries ?treaty_OJDate ?treaty_pdf ?treaty_OJ ?location ?location_country ?location_uri
WHERE{{ ?treatyType_uri skos:prefLabel ?treatyType;
       skos:definition ?treatyType_definition.
       ?treaty_uri dcterms:type ?treatyType_uri;
       skos:prefLabel ?treaty;
       euvoc:startDate ?treaty_startDate.
       OPTIONAL{
           ?treaty_uri ontology:first_date_entry_in_force ?treaty_dateEIF;
           euvoc:officialJournalNumber ?treaty_OJNumber;
           euvoc:officialJournalSeries ?treaty_OJSeries;
           euvoc:officialJournalDate ?treaty_OJDate.
           BIND (YEAR(?treaty_OJDate) AS ?treaty_OJYear).
           BIND (CONCAT("https://eur-lex.europa.eu/legal-content/EN/TXT/?uri=OJ:",?treaty_OJSeries,":",?treaty_OJYear, ":", ?treaty_OJNumber,":TOC") AS ?treaty_OJ).
       }
       OPTIONAL{
           ?treaty_uri euvoc:signatureLocation ?location_uri.
           ?location_uri skos:prefLabel ?location;
            geosparql:sfWithin ?location_countryUri.
           ?location_countryUri skos:prefLabel ?location_country.
           FILTER(langMatches(lang(?location), "en")).
       }
       OPTIONAL{
           ?treaty_uri authority:celex_root ?treaty_celexNum.    
            BIND (IF(?treaty_uri in (<http://publications.europa.eu/resource/authority/treaty/TEEC_1957>,
            <http://publications.europa.eu/resource/authority/treaty/CECA>,
            <http://publications.europa.eu/resource/authority/treaty/CECA_1951>,
            <http://publications.europa.eu/resource/authority/treaty/TEEC>),
            CONCAT("https://eur-lex.europa.eu/legal-content/","FR","/TXT/PDF/?uri=CELEX:",?treaty_celexNum,"/TXT&from=EN"),CONCAT("https://eur-lex.europa.eu/legal-content/","EN","/TXT/PDF/?uri=CELEX:",?treaty_celexNum,"/TXT&from=EN")) AS ?treaty_pdf)
       }
      } 
      FILTER (?treatyType_uri=<PARAMETER>).
      FILTER(langMatches(lang(?treatyType), "en")).
      FILTER(langMatches(lang(?treaty), "en")).
      FILTER(langMatches(lang(?treatyType_definition), "en")).
      FILTER(langMatches(lang(?location_country), "en")).
     }

PREFIX skos:<http://www.w3.org/2004/02/skos/core#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX euvoc: <http://publications.europa.eu/ontology/euvoc#>
PREFIX ontology:<http://data.europa.eu/eli/ontology#>
PREFIX authority:<http://publications.europa.eu/ontology/authority/>
PREFIX geo:<http://www.w3.org/2003/01/geo/wgs84_pos#>
PREFIX org:<http://www.w3.org/ns/org#>
PREFIX vcard:<http://www.w3.org/2006/vcard/ns#>
PREFIX geosparql:<http://www.opengis.net/ont/geosparql#>

SELECT DISTINCT ?location ?location_uri ?classificationOrg ?classificationOrg_uri ?corporateBody ?corporateBody_uri ?corporateBody_webpage ?corporateBody_site ?corporateBody_email ?corporateBody_telephone ?corporateBody_address ?broaderLabel WHERE{{ 
?location_uri skos:prefLabel ?location.
#geosparql:sfWithin ?location_countryUri.
#?location_countryUri skos:prefLabel ?location_country.
?corporateBody_uri geo:location ?location_uri;
org:classification ?classificationOrg_uri;
skos:prefLabel ?corporateBody.
?classificationOrg_uri skos:prefLabel ?classificationOrg_label.
OPTIONAL{
?corporateBody_uri skos:broader+ ?broader.
?broader skos:prefLabel ?broaderLabel.
FILTER(langMatches(lang(?broaderLabel), "en")).
}
OPTIONAL{
?corporateBody_uri <http://xmlns.com/foaf/0.1/homepage> ?corporateBody_webpage.
}
OPTIONAL{
?corporateBody_site org:siteOf ?corporateBody_uri;
vcard:hasEmail ?corporateBody_email;
vcard:hasTelephone ?bnodeTel;
vcard:hasAddress ?bnodeAddress.
?bnodeTel vcard:hasValue ?corporateBody_telephone.
?bnodeAddress vcard:locality ?locality;
vcard:postal-code ?postalCode;
vcard:street-address ?streetAddress.
BIND (CONCAT(?streetAddress,". ",?postalCode," ", ucase(?locality)) AS ?corporateBody_address).
}
BIND (IF(?classificationOrg_uri in (<http://publications.europa.eu/resource/authority/corporate-body-classification/CB_INT>),
'International organisation',IF(?broaderLabel,?broaderLabel,?classificationOrg_label)) AS ?classificationOrg)
}
FILTER (?location_uri=<PARAMETER>).
FILTER(langMatches(lang(?location), "en")).
FILTER(langMatches(lang(?corporateBody), "en")).
#FILTER(langMatches(lang(?location_country), "en")).
FILTER(langMatches(lang(?classificationOrg_label), "en")).
}ORDER BY ?location ?classificationOrg ?corporateBody

PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT DISTINCT ?DEUCatalog_Modified ?DEUCatalog_Language ?DEUCatalog_uri concat(?title, ' - ', ?description) as ?DEUCatalog ?datatheme ?datatheme_uri ?datatheme_definition ?dataset ?dataset_uri (?dataset_uri AS ?dataset_link) ?dataset_description ?dataset_accessRights ?documentation ?documentation_description ?documentation_uri (?documentation_uri AS ?documentation_link)
  WHERE{{ 
        ?DEUCatalog_uri dcat:dataset ?dataset_uri; 
        dcterms:title ?title.
        ?dataset_uri dcterms:description ?dataset_description;
        dcterms:title ?dataset.
        #dcterms:accessRights ?dataset_accessRightsURI.
        #?dataset_accessRightsURI skos:prefLabel ?dataset_accessRights.
        #?dataset_uri dcat:theme ?datatheme_uri.
        #?dataset_uri dcat:distribution ?distribution_uri.
        #?datatheme_uri skos:prefLabel ?datatheme.
